package errorchecking;

public class Pattern_program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1,j=2, row=5;
		
		for (i=0;i<row;i++)
		{
		for (j=6;j<=i;j++)
		{
			System.out.print("*");
		}
		
		System.out.println();
	}
	}
}
		
		
	
		
		


